import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { games } from '../games';

function FavoriteGames() {
  const navigate = useNavigate();
  const { user, removeFavorite } = useAuth();

  const [favoriteGames, setFavoriteGames] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [modalMessage, setModalMessage] = useState('');

  useEffect(()=>{
        let fg = user?.favorites.map( favoriteId => {
            return games.find( g => g.id == favoriteId )
        });
        fg = fg || [];
        fg.forEach( game => {
          const myPlay = user.todayPlays[game.id];
          game.scores = {
            me: myPlay
          };
        });

        setFavoriteGames(fg);

  },[games, user]);

  const onClickEditPlay = (e)=> {
    const id = e.currentTarget.dataset.id;
    navigate(`/edit-score/${id}`);
  }

  const onClickShowMessage = (e, message) => {
    e.stopPropagation(); // Prevent navigation
    setModalMessage(message);
    setShowModal(true);
  }

  const closeModal = () => {
    setShowModal(false);
    setModalMessage('');
  }

  return (
    <section id="favorite-games" className="max-w-4xl mx-auto p-4">
      <ul id="favorite-games-list" className="list-none p-0 space-y-2">
        { favoriteGames?.map( f => {
          // Determine if there's a score to display
          const hasScore = !!f.scores?.me;

          // Create the score/edit button element
          let myScore;
          if (hasScore) {
            const hasMessage = f.scores.me?.message && f.scores.me.message.trim() !== '';
            myScore = (
              <div className="relative">
                <button
                  data-id={f.id}
                  data-message={f.scores.me?.message || ''}
                  onClick={hasMessage ? (e) => onClickShowMessage(e, f.scores.me.message) : onClickEditPlay}
                  className="bg-blue-600 text-white px-3 py-2 rounded hover:bg-[#282850] transition-colors duration-200 font-medium relative"
                  title={hasMessage ? "Click to view message" : "Click to edit score"}
                >
                  {f.scores.me?.score}
                  {hasMessage && (
                    <svg
                      className="absolute -top-1 -right-1 w-4 h-4 bg-yellow-400 text-gray-800 rounded-full p-0.5"
                      viewBox="0 0 24 24"
                      fill="currentColor"
                    >
                      <path d="M20 2H4c-1.1 0-2 .9-2 2v12c0 1.1.9 2 2 2h4l4 4 4-4h4c1.1 0 2-.9 2-2V4c0-1.1-.9-2-2-2z"/>
                    </svg>
                  )}
                </button>
                {hasMessage && (
                  <button
                    data-id={f.id}
                    onClick={onClickEditPlay}
                    className="ml-1 text-xs bg-gray-500 text-white px-2 py-1 rounded hover:bg-gray-600 transition-colors duration-200"
                    title="Edit score"
                  >
                    Edit
                  </button>
                )}
              </div>
            );
          } else {
            myScore = (
              <button
                data-id={f.id}
                onClick={onClickEditPlay}
                className="w-8 h-8 rounded bg-blue-600 text-white flex items-center justify-center hover:bg-[#282850] transition-colors duration-200 shadow-sm"
                title="Add score"
              >
                <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                  <path d="M17 3a2.85 2.83 0 1 1 4 4L7.5 20.5 2 22l1.5-5.5Z"/>
                  <path d="m15 5 4 4"/>
                </svg>
              </button>
            );
          }

          return (
            <li key={f.id} className="bg-white text-black rounded-lg shadow-sm py-1 px-2 hover:shadow-lg transition-all duration-300 flex justify-between items-center">
              <a href={f.url} className="block flex hover:text-blue-600 items-center">
                <img src={"./img/games/"+f.image} className="w-12 h-12 rounded-md border-2 border-black mr-1"/>
                <p className="font-bold text-left text-lg">{f.name}</p>
              </a>
              {myScore}
            </li>
          );
        }) }
      </ul>

      {/* Modal Dialog */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50" onClick={closeModal}>
          <div className="bg-white rounded-lg p-6 max-w-sm mx-4 shadow-xl" onClick={(e) => e.stopPropagation()}>
            <div className="flex justify-between items-start mb-4">
              <h3 className="text-lg font-semibold text-gray-900">Score Message</h3>
              <button
                onClick={closeModal}
                className="text-gray-400 hover:text-gray-600 text-xl font-bold"
              >
                Ã—
              </button>
            </div>
            <p className="text-gray-700 mb-4">{modalMessage}</p>
            <button
              onClick={closeModal}
              className="w-full bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-700 transition-colors duration-200"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </section>
  );
}

export default FavoriteGames;
