// import { useState, useEffect } from 'react';
import { useData } from '../utils/DataContext';

function AllGames() {

  const { userData, games, setPreferences, addFavorite, removeFavorite } = useData();

  const onClickFavorite = (e)=> {
    const id = parseInt(e.target.dataset.id);
    console.log("favorite",id);
    addFavorite(id);
  }
  const onClickUnfavorite = (e)=> {
    const id = parseInt(e.target.dataset.id);
    console.log("unfavorite",id);
    removeFavorite(id);
  }

  return (
    <section id="other-games">
      <ul id="other-games-list">
        { games?.map( f=> {
          const isFavorite = userData?.favorites.includes(Number.parseInt(f.id))
          console.log(f.id+":"+isFavorite,userData);
          let addRemoveButton = <button data-id={f.id} onClick={onClickFavorite}>+</button>;
          if (isFavorite) addRemoveButton = <button data-id={f.id} onClick={onClickUnfavorite}>-</button>;
          return <li key={f.id}>
            {f.name}    
            {addRemoveButton}
          </li> 
        })}
      </ul>
    </section>
  );

}

export default AllGames;
