import { useState } from 'react';
import { useData } from '../utils/DataContext';

function Friends() {

  const { userData, addFriend, removeFriend } = useData();

  const [newFriendName, setNewFriendName] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  const onClickRemove = (e)=> {
    const id = e.target.dataset.id;
    removeFriend(id);
  }

  const onClickAdd = async (e)=> {
    const added = await addFriend(newFriendName);
    if (added){
      setNewFriendName("");
    }
  }

  return (
    <section id="friends" className="max-w-4xl mx-auto p-4">
      <h2 className="text-2xl font-bold text-center text-gray-800 mb-6">Friends</h2>

      {/* Friends List */}
      <ul id="friends-list" className="list-none p-0 space-y-2 mb-8">
        { userData?.friends?.map( f => {
          console.log("?"+f.id,f)
          return (
            <li key={f.id} className="bg-white text-black rounded-lg shadow-sm py-2 px-4 hover:shadow-lg transition-all duration-300 flex justify-between items-center">
              <span className="font-medium">{f.username}</span>
              <button
                data-id={f.id}
                onClick={onClickRemove}
                className="w-6 h-6 rounded-full bg-red-500 text-white flex items-center justify-center hover:bg-red-600 transition-colors duration-200 shadow-sm font-bold"
              >
                -
              </button>
            </li>
          );
        }) }
      </ul>

      {/* Add Friend Form */}
      <div className="bg-white rounded-lg shadow-md p-4">
        <div className="flex items-center space-x-2">
          <input
            id="new-friend-name"
            placeholder="Enter username to add"
            value={newFriendName}
            onChange={(e)=>{ setNewFriendName(e.target.value) }}
            className="flex-grow px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors"
          />
          <button
            onClick={onClickAdd}
            className="w-8 h-8 rounded-full bg-green-500 text-white flex items-center justify-center hover:bg-green-600 transition-colors duration-200 shadow-sm font-bold text-lg"
          >
            +
          </button>
        </div>
        <p>{errorMessage}</p>
      </div>
    </section>
  );
}

export default Friends;
