// import { useState, useEffect } from 'react';
import { useData } from '../utils/DataContext';

function AllGames() {

  const { userData, games, setPreferences, addFavorite, removeFavorite } = useData();

  const onClickFavorite = (e)=> {
    const id = parseInt(e.target.dataset.id);
    console.log("favorite",id);
    addFavorite(id);
  }
  const onClickUnfavorite = (e)=> {
    const id = parseInt(e.target.dataset.id);
    removeFavorite(id);
  }

  return (
    <section id="other-games" className="max-w-4xl mx-auto p-4">
      <ul id="other-games-list" className="list-none p-0 space-y-2">
        { games?.map( f=> {
          const isFavorite = userData?.favorites.includes(Number.parseInt(f.id));
          const buttonColors = isFavorite ? `rotate-45 bg-red-500 hover:bg-red-600` : `bg-green-500 hover:bg-green-600`;
          const itemColors = isFavorite ? `bg-white` : `bg-gray-300`;
          const buttonClasses = `text-2xl w-6 h-6 rounded-full transition-all duration-800 flex items-center justify-center shadow-sm text-white font-bold ${buttonColors}`;

          const addRemoveButton = (
            <button
              data-id={f.id}
              onClick={isFavorite ? onClickUnfavorite : onClickFavorite}
              className={buttonClasses}
            >
              <img src='./plus.svg' className='w-4 h-4 pointer-events-none'/>
            </button>
          );

          return (
            <li key={f.id} className={itemColors+" text-black hover:text-opacity-40 transition-all duration-800 rounded-lg shadow-sm py-1 px-2 hover:shadow-lg flex justify-between items-center"}>
              <a href={f.url} className="font-medium">{f.name}</a>
              {addRemoveButton}
            </li>
          );
        })}
      </ul>
    </section>
  );

}

export default AllGames;
