import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { games } from '../games';

function FavoriteGames() {
  const navigate = useNavigate();
  const { user, removeFavorite } = useAuth();

  const [favoriteGames, setFavoriteGames] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [modalMessage, setModalMessage] = useState('');
  const [modalFriendName, setModalFriendName] = useState('');

  useEffect(()=>{
        let fg = user?.favorites.map( favoriteId => {
            return games.find( g => g.id == favoriteId )
        });
        fg = fg || [];
        fg.forEach( game => {
          const myPlay = user.todayPlays[game.id];
          game.scores = {
            me: myPlay,
            friends: {}
          };

          // Add friends' scores
          user.friends?.forEach(friend => {
            const friendPlay = friend.todayPlays?.[game.id];
            game.scores.friends[friend.id] = {
              play: friendPlay,
              friend: friend
            };
          });
        });

        setFavoriteGames(fg);

  },[games, user]);

  const onClickEditPlay = (e)=> {
    const id = e.currentTarget.dataset.id;
    navigate(`/edit-score/${id}`);
  }

  const onClickShowMessage = (e, message, friendName = '') => {
    e.stopPropagation(); // Prevent navigation
    setModalMessage(message);
    setModalFriendName(friendName);
    setShowModal(true);
  }

  const closeModal = () => {
    setShowModal(false);
    setModalMessage('');
    setModalFriendName('');
  }

  return (
    <section id="favorite-games" className="max-w-4xl mx-auto p-4">
      <ul id="favorite-games-list" className="list-none p-0 space-y-2">
        { favoriteGames?.map( f => {
          // Create score blocks for user and friends
          const createScoreBlock = (play, isUser = false, friend = null) => {
            if (play) {
              const hasMessage = play.message && play.message.trim() !== '';
              return (
                <button
                  key={isUser ? 'user' : friend.id}
                  data-id={f.id}
                  data-message={play.message || ''}
                  onClick={isUser ? onClickEditPlay : (hasMessage ? (e) => onClickShowMessage(e, play.message, friend.username) : undefined)}
                  className={`${isUser ? 'bg-blue-600 hover:bg-[#282850]' : 'bg-gray-500 hover:bg-gray-600'} text-white px-2 py-1 rounded transition-colors duration-200 font-medium relative text-sm min-w-[2rem] ${!isUser && !hasMessage ? 'cursor-default' : 'cursor-pointer'}`}
                  title={isUser ? "Click to edit score" : (hasMessage ? `${friend.username}: Click to view message` : `${friend.username}: ${play.score}`)}
                >
                  {play.score}
                  {hasMessage && (
                    <svg
                      className="absolute -top-1 -right-1 w-3 h-3 bg-yellow-400 text-gray-800 rounded-full p-0.5"
                      viewBox="0 0 24 24"
                      fill="currentColor"
                    >
                      <path d="M20 2H4c-1.1 0-2 .9-2 2v12c0 1.1.9 2 2 2h4l4 4 4-4h4c1.1 0 2-.9 2-2V4c0-1.1-.9-2-2-2z"/>
                    </svg>
                  )}
                </button>
              );
            } else {
              // Placeholder for missing score
              if (isUser) {
                return (
                  <button
                    key="user"
                    data-id={f.id}
                    onClick={onClickEditPlay}
                    className="w-8 h-8 rounded bg-blue-600 text-white flex items-center justify-center hover:bg-[#282850] transition-colors duration-200 shadow-sm"
                    title="Add score"
                  >
                    <svg width="12" height="12" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                      <path d="M17 3a2.85 2.83 0 1 1 4 4L7.5 20.5 2 22l1.5-5.5Z"/>
                      <path d="m15 5 4 4"/>
                    </svg>
                  </button>
                );
              } else {
                return (
                  <div
                    key={friend.id}
                    className="w-8 h-8 rounded bg-gray-300 flex items-center justify-center text-gray-500 text-xs"
                    title={`${friend.username}: No score`}
                  >
                    -
                  </div>
                );
              }
            }
          };

          // Create all score blocks
          const scoreBlocks = [];

          // User's score (leftmost)
          scoreBlocks.push(createScoreBlock(f.scores?.me, true));

          // Friends' scores
          Object.values(f.scores?.friends || {}).forEach(({ play, friend }) => {
            scoreBlocks.push(createScoreBlock(play, false, friend));
          });

          return (
            <li key={f.id} className="bg-white text-black rounded-lg shadow-sm py-1 px-2 hover:shadow-lg transition-all duration-300 flex justify-between items-center">
              <a href={f.url} className="block flex hover:text-blue-600 items-center">
                <img src={"./img/games/"+f.image} className="w-12 h-12 rounded-md border-2 border-black mr-1"/>
                <p className="font-bold text-left text-lg">{f.name}</p>
              </a>
              <div className="flex items-center space-x-1">
                {scoreBlocks}
              </div>
            </li>
          );
        }) }
      </ul>

      {/* Modal Dialog */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50" onClick={closeModal}>
          <div className="bg-white rounded-lg p-6 max-w-sm mx-4 shadow-xl" onClick={(e) => e.stopPropagation()}>
            <div className="flex justify-between items-start mb-4">
              <h3 className="text-lg font-semibold text-gray-900">
                {modalFriendName ? `${modalFriendName}'s Message` : 'Score Message'}
              </h3>
              <button
                onClick={closeModal}
                className="text-gray-400 hover:text-gray-600 text-xl font-bold"
              >
                Ã—
              </button>
            </div>
            <p className="text-gray-700 mb-4">{modalMessage}</p>
            <button
              onClick={closeModal}
              className="w-full bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-700 transition-colors duration-200"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </section>
  );
}

export default FavoriteGames;
