import { useState, useRef, useEffect } from 'react';
import { useData } from '../utils/DataContext';
import { useNavigate } from 'react-router-dom';
import { HexColorPicker } from 'react-colorful';

function Signup() {

    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [username, setUsername] = useState("");
    const [character, setCharacter] = useState("");
    const [color, setColor] = useState("#333366"); // Default to app's blue color

    // State for showing/hiding pickers
    const [showEmojiPicker, setShowEmojiPicker] = useState(false);
    const [showColorPicker, setShowColorPicker] = useState(false);

    // Popular emojis for quick selection
    const popularEmojis = ["ðŸ˜€", "ðŸ˜Ž", "ðŸš€", "ðŸ”¥", "â­", "ðŸŽ®", "ðŸŽ¯", "ðŸ†", "ðŸŽ²", "ðŸŽª", "ðŸŽ­", "ðŸŽ¨", "ðŸŽ§", "ðŸ“±", "ðŸ’»", "ðŸŒˆ", "ðŸ¦„", "ðŸ±", "ðŸ¶", "ðŸ¦Š"];

    // Refs for click-outside detection
    const emojiPickerRef = useRef(null);
    const colorPickerRef = useRef(null);

    const { signUpWithEmail } = useData();
    const navigate = useNavigate();

    // Handle clicking outside the pickers to close them
    useEffect(() => {
        function handleClickOutside(event) {
            if (emojiPickerRef.current && !emojiPickerRef.current.contains(event.target)) {
                setShowEmojiPicker(false);
            }
            if (colorPickerRef.current && !colorPickerRef.current.contains(event.target)) {
                setShowColorPicker(false);
            }
        }

        document.addEventListener("mousedown", handleClickOutside);
        return () => {
            document.removeEventListener("mousedown", handleClickOutside);
        };
    }, []);

    const signUp = () => {
        signUpWithEmail(email, password, username, character, color, ()=>navigate('/') );
    }

    // Handle emoji selection
    const selectEmoji = (emoji) => {
        setCharacter(emoji);
        setShowEmojiPicker(false);
    };

    return (
        <section id="signup" className="max-w-md mx-2 mt-10 p-6 bg-white rounded-lg shadow-lg">
            <h3 className="text-2xl font-bold text-center text-gray-800 mb-6">Create Account</h3>

            <div className="space-y-4">
                <div>
                    <label htmlFor="signup-email" className="block text-sm font-medium text-gray-700 mb-1">Email</label>
                    <input
                        id="signup-email"
                        type="email"
                        placeholder="your@email.com"
                        value={email}
                        onChange={(e)=>{ setEmail(e.target.value) }}
                        className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors"
                    />
                </div>

                <div>
                    <label htmlFor="signup-password" className="block text-sm font-medium text-gray-700 mb-1">Password</label>
                    <input
                        type="password"
                        id="signup-password"
                        placeholder="â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢"
                        value={password}
                        onChange={(e)=>{ setPassword(e.target.value) }}
                        className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors"
                    />
                </div>

                <div>
                    <label htmlFor="signup-username" className="block text-sm font-medium text-gray-700 mb-1">Username</label>
                    <input
                        id="signup-username"
                        placeholder="Choose a username"
                        value={username}
                        onChange={(e)=>{ setUsername(e.target.value) }}
                        className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors"
                    />
                </div>

                <div className="grid grid-cols-2 gap-4">
                    <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">Emoji</label>
                        <div className="relative" ref={emojiPickerRef}>
                            <button
                                type="button"
                                onClick={() => setShowEmojiPicker(!showEmojiPicker)}
                                className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors flex items-center justify-between"
                            >
                                <span className="text-xl">{character || "Select emoji"}</span>
                                <svg className="w-5 h-5 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 9l-7 7-7-7"></path>
                                </svg>
                            </button>

                            {showEmojiPicker && (
                                <div className="absolute z-10 mt-1 bg-white p-3 rounded-lg shadow-lg border border-gray-200 w-64">
                                    <div className="grid grid-cols-5 gap-2">
                                        {popularEmojis.map((emoji, index) => (
                                            <button
                                                key={index}
                                                onClick={() => selectEmoji(emoji)}
                                                className="text-2xl h-10 w-10 flex items-center justify-center hover:bg-gray-100 rounded-md transition-colors"
                                            >
                                                {emoji}
                                            </button>
                                        ))}
                                    </div>
                                </div>
                            )}
                        </div>
                    </div>

                    <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">Color</label>
                        <div className="relative" ref={colorPickerRef}>
                            <button
                                type="button"
                                onClick={() => setShowColorPicker(!showColorPicker)}
                                className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors flex items-center justify-between"
                            >
                                <div className="flex items-center">
                                    <div
                                        className="w-6 h-6 rounded mr-2 border border-gray-300"
                                        style={{ backgroundColor: color }}
                                    ></div>
                                    <span>{color}</span>
                                </div>
                                <svg className="w-5 h-5 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 9l-7 7-7-7"></path>
                                </svg>
                            </button>

                            {showColorPicker && (
                                <div className="absolute z-10 mt-1">
                                    <div className="p-4 bg-white rounded-lg shadow-lg border border-gray-200">
                                        <HexColorPicker
                                            color={color}
                                            onChange={setColor}
                                            style={{ width: '100%', height: '180px' }}
                                        />
                                        <div className="mt-3 flex justify-between items-center">
                                            <div
                                                className="w-8 h-8 rounded-md border border-gray-300 mr-2"
                                                style={{ backgroundColor: color }}
                                            ></div>
                                            <input
                                                type="text"
                                                value={color}
                                                onChange={(e) => setColor(e.target.value)}
                                                className="flex-1 px-2 py-1 text-sm border border-gray-300 rounded"
                                            />
                                        </div>
                                    </div>
                                </div>
                            )}
                        </div>
                    </div>
                </div>

                <button
                    onClick={signUp}
                    className="w-full bg-blue-600 text-white py-2 px-4 rounded-md hover:bg-[#282850] focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 transition-colors duration-200 font-medium mt-2"
                >
                    Create Account
                </button>

                <div className="text-center mt-4">
                    <p className="text-sm text-gray-600">
                        Already have an account?{' '}
                        <a href="/login" className="text-blue-600 hover:text-blue-800 font-medium transition-colors">
                            Sign In
                        </a>
                    </p>
                </div>
            </div>
        </section>
    );
}

export default Signup;
